import React from 'react';
import { useState, useEffect } from 'react';
import { getAllProfiles, deleteProfile } from './api';
import './App.css'; // Archivo CSS para los estilos

function App() {

  const [profiles, setProfiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const data = await getAllProfiles(); // Llama a getAllProfiles y espera la respuesta
        setProfiles(data.object); // Actualiza el estado con la lista de perfiles
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

