import { useEffect, useState } from "react";

const BASE_URL = "http://localhost:8092/api";

export function showAll() {
    const [data, setData] = useState(null);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(url);
                const jsonData = await response.json();
                setData(jsonData);
            } catch (error) {
                setError(error);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, [url]);

    return { mensaje: data?.mensaje, object: data?.object, error, loading };
}

// Funci贸n para eliminar un perfil por ID
export const deleteProfile = async (id) => {
    try {
        const response = await fetch(`${BASE_URL}/delete/${id}`, {
            method: 'DELETE',
        });
        if (!response.ok) {
            throw new Error('Error al eliminar el perfil');
        }
        return true; // Retorna verdadero si la eliminaci贸n fue exitosa
    } catch (error) {
        console.error('Error al eliminar el perfil:', error.message);
        throw error;
    }
};
