import React from 'react';
import { useState, useEffect } from 'react';
import { getAllProfiles, deleteProfile } from './api';
import './App.css'; // Archivo CSS para los estilos

function App() {

  const [profiles, setProfiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showCreatePopup, setShowCreatePopup] = useState(false);
  const [newUserData, setNewUserData] = useState({
    nombre: '',
    apellidos: '',
    curso: '',
    anoIngreso: '',
    notaMedia: '',
  });

  const fetchData = async () => {
    try {
      setLoading(true);
      const data = await getAllProfiles();
      setProfiles(data.object);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    try {
      setLoading(true);
      await deleteProfile(id);
      const updatedProfiles = profiles.filter(profile => profile.id !== id);
      setProfiles(updatedProfiles);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  const handleCreate = async () => {
    try {
      setLoading(true);
      // Aquí debes ajustar createProfile para enviar newUserData al backend
      // await createProfile(newUserData);
      // Después de crear el usuario, podrías actualizar la lista de perfiles o realizar otra acción necesaria
      // Por ejemplo, cerrar el popup y limpiar el formulario
      setShowCreatePopup(false);
      setNewUserData({
        nombre: '',
        apellidos: '',
        curso: '',
        anoIngreso: '',
        notaMedia: '',
      });
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const closeModal = () => {
    setError(null);
  };

  return (
    <div className="app-container">
      {loading && <p>Cargando...</p>}
      {profiles ? (<div>
        <p className="warning">No hay registros</p>
        <div className="buttons">
          <button className="create-button" onClick={() => handleCreate(profile.id)}>Crear Usuario</button>
        </div>
      </div>)
      :(profiles && (
        profiles.map(profile => (
          <div key={profile.id} className="profile-card">
            <div className="data">
              <p className="profile-name">{profile.nombre} {profile.apellidos}</p>
              <p>Curso: {profile.curso}</p>
              <p>Año de Ingreso: {profile.anoIngreso}</p>
              <p>Nota Media: {profile.notaMedia}</p>
            </div>
            <div className="buttons">
              <button className="delete-button" onClick={() => handleDelete(profile.id)}>Eliminar</button>
            </div>
          </div>
        ))
      ))}
      {error && (
        <div className="popUp">
          <div className="popUp-content">
            <span className="close" onClick={closeModal}>&times;</span>
            <p className="error-message">{error}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
