import React from 'react';
import { useState, useEffect } from 'react';
import { getAllProfiles, deleteProfile } from './api';
import './App.css'; // Archivo CSS para los estilos

function App() {

  const [profiles, setProfiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const data = await getAllProfiles();
        setProfiles(data.object);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleDelete = async (id) => {
    try {
      setLoading(true);
      await deleteProfile(id);
      // Una vez eliminado el perfil, actualiza la lista de perfiles
      const updatedProfiles = profiles.filter(profile => profile.id !== id);
      setProfiles(updatedProfiles);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      {loading && <p>Cargando...</p>}
      {profiles && (
        profiles.map(profile => (
          <div key={profile.id} className="profile-card">
            <div className="data">
              <p className="profile-name">{profile.nombre} {profile.apellidos}</p>
              <p>Curso: {profile.curso}</p>
              <p>AÃ±o de Ingreso: {profile.anoIngreso}</p>
              <p>Nota Media: {profile.notaMedia}</p>
            </div>
            <div className="buttons">
              <button className="delete-button" onClick={() => handleDelete(profile.id)}>Eliminar</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default App;
