import React from 'react';
import {getAllProfiles,deleteProfile} from './api';
import './App.css'; // Archivo CSS para los estilos

function App() {
  const { mensaje, object, error } = useFetch("http://localhost:8092/api/showAll");

  const handleDelete = (id) => {
    useFetch("http://localhost:8092/api/delete/"+id);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <div className="error">{error}</div>;
  if (mensaje) return <div className="message">{mensaje}</div>; // Estilo para mensaje

  return (
    <div className="app-container">
      {object && Array.isArray(object) && (
        object.map(profile => (
          <div key={profile.id} className="profile-card">
            <div class="data">
              <p className="profile-name">{profile.nombre} {profile.apellidos}</p>
              <p>Curso: {profile.curso}</p>
              <p>AÃ±o de Ingreso: {profile.anoIngreso}</p>
              <p>Nota Media: {profile.notaMedia}</p>
            </div>
            <div class="buttons">
              <button className="delete-button" onClick={() => handleDelete(profile.id)}>Eliminar</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default App;
